import React from 'react';

import Todo from '../models/todo';
import TodoItem from './TodoItem';
import classes from './Todos.module.css';

const Todos: React.FC<{ items: Todo[]; saveUpdatedItems: (newItems: Todo[]) => void }> = (props) => {
    const deleteItemHandler = (id: string) => {
        const newItems = props.items.filter((item) => item.id !== id);

        props.saveUpdatedItems(newItems);
    };
    return (
        <ul className={classes.todos}>
            {props.items.map((item) => (
                <TodoItem key={item.id} item={item} deleteItem={deleteItemHandler} />
            ))}
        </ul>
    );
};

export default Todos;
