import React from 'react';
import classes from './TodoItem.module.css';
import Todo from '../models/todo';

const TodoItem: React.FC<{ item: Todo; deleteItem: (id: string) => void }> = (props) => {
    const onClickDeleteBtn = (id: string) => {
        props.deleteItem(id);
    };

    return (
        <>
            <li className={classes.item} onClick={() => onClickDeleteBtn(props.item.id)}>
                {props.item.text}
            </li>
        </>
    );
};

export default TodoItem;
